NORMALGEN.convert = function(heightImage, params) {

    // init renderer
	var normalCanvas = NORMALGEN.normalCanvas = NORMALGEN.normalCanvas ||
		document.createElement('canvas');
	var renderer = NORMALGEN.renderer = NORMALGEN.renderer ||
		new v3d.WebGLRenderer({ alpha: false, antialias: true, canvas: normalCanvas });
	renderer.setSize(params.width, params.height);

	// init normal map shader
	var normalShader = NORMALGEN.NormalMapShader;
	var normalUniforms = v3d.UniformsUtils.clone(normalShader.uniforms);

    // set width and height
    normalUniforms['dimensions'].value =
        [params.width, params.height, 0];

    // set strength and level
    normalUniforms['dz'].value =
        1.0 / params.strength * (1.0 + Math.pow(2.0, params.level));

    // set filter
    normalUniforms['type'].value = (params.filter == 'Sobel') ? 0 : 1;

    // set inversion flags
    normalUniforms['invertR'].value = params.invertR ? -1 : 1;
    normalUniforms['invertG'].value = params.invertG ? -1 : 1;
    normalUniforms['invertH'].value = params.invertHeight ? -1 : 1;

    // setup texture
	var heightTex		  = new v3d.Texture(heightImage);
	heightTex.minFilter   = heightTex.magFilter = v3d.NearestFilter;
	heightTex.needsUpdate = true;
	normalUniforms['tHeightMap'].value = heightTex;

    // setup material
	var normalMat = new v3d.ShaderMaterial({
		fragmentShader: normalShader.fragmentShader,
		vertexShader: normalShader.vertexShader,
		uniforms: normalUniforms
	});

    // setup mesh
	var geometry = new v3d.PlaneBufferGeometry(1, 1, 1, 1);
	var mesh = new v3d.Mesh(geometry, normalMat);
	mesh.name = 'NORMALGEN_MESH';

    // setup scene
	var scene = new v3d.Scene();
	scene.add(mesh);
	var camera = new v3d.OrthographicCamera(1/-2, 1/2, 1/2, 1/-2, 0, 1);
	var normalPass = new v3d.RenderPass(scene, camera);

	// blur passes
	var gaussianPassY = new v3d.ShaderPass(NORMALGEN.VerticalBlurShader);
	var gaussianPassX = new v3d.ShaderPass(NORMALGEN.HorizontalBlurShader);

    // set blur params
	gaussianPassY.uniforms['v'].value = params.blur / params.width / 5;
	gaussianPassX.uniforms['h'].value = params.blur / params.height / 5;

    // setup render target
	var rtParams = { minFilter: v3d.NearestFilter, magFilter: v3d.NearestFilter,
	    format: v3d.RGBAFormat, stencilBufer: false };
	var renderTarget = new v3d.WebGLRenderTarget(params.width, params.height, rtParams);

    // setup composer
	var composer = new v3d.EffectComposer(renderer, renderTarget);
	composer.setSize(params.width, params.height);
	composer.addPass(normalPass);
	composer.addPass(gaussianPassY);
	composer.addPass(gaussianPassX);

    // draw
    composer.render(1/60);

	// retrieve image
	var resultImage = normalCanvas.toDataURL();

	// clean up
	normalMat.dispose();
	scene.dispose();
	composer.dispose();
	renderer.dispose();

	return resultImage;
}


