			'lef1 = lef1 >= 0.0 ? lef1 : (1.0 + lef1);',
			'lef1 = lef1 < 1.0  ? lef1 : (lef1 - 1.0 );',
			'rig1 = rig1 >= 0.0 ? rig1 : (1.0 + rig1);',
			'rig1 = rig1 < 1.0  ? rig1 : (rig1 - 1.0 );',
			'rig2 = rig2 >= 0.0 ? rig2 : (1.0 + rig2);',
